INSERT INTO `stats_sql` VALUES ('STEALS', 'PTO', 'Steals Per Turnover', 20, '	SELECT 	 PL.FNAME\r\n		,PL.NAME\r\n		,TM.CITYNAME\r\n		,TM.TEAMNAME\r\n		,TM.TEAMNUM\r\n		,PS.PLAYERID\r\n		,PS.STATS_TOTAL\r\n		,PS.STATS_TOTAL_1\r\n		,PS.STAT_PG\r\n		,PS.GAME_COUNT\r\n\r\n	FROM (\r\n	SELECT	 PS.PLAYERID AS PLAYERID\r\n		,SUM(PS.STEALS) as STATS_TOTAL\r\n		,SUM(PS.TURNOVERS) as STATS_TOTAL_1\r\n		,COALESCE((SUM(PS.STEALS + 0.00) / SUM(PS.TURNOVERS + 0.00)),0) as STAT_PG\r\n		,SUM(PS.PLAYED) as GAME_COUNT\r\n\r\n	FROM PLAYERSTATS PS\r\n\r\n	INNER JOIN SCHEDULE SH\r\n	ON PS.SCHEDULE_ID = SH.SCHEDULE_ID\r\n	AND PS.PLAYED = 1\r\n\r\n	INNER JOIN SEASON SE\r\n	ON SH.GAME_DATE BETWEEN SE.SEASON_SDTE AND SE.SEASON_EDTE\r\n	AND CURDATE() BETWEEN SE.SEASON_SDTE AND SE.SEASON_EDTE\r\n	GROUP BY 1\r\n\r\n	ORDER BY STAT_PG DESC, STATS_TOTAL DESC\r\n	LIMIT 50) AS PS\r\n\r\n	INNER JOIN PLAYERS PL\r\n	ON PS.PLAYERID = PL.PLAYERID\r\n\r\n	INNER JOIN TEAMS TM\r\n	ON PL.TEAM = TM.TEAMNUM\r\n\r\n	ORDER BY PS.STAT_PG DESC, PS.STATS_TOTAL DESC, PS.GAME_COUNT, PL.NAME, PL.FNAME\r\n', 0);
