INSERT INTO `stats_sql` VALUES ('TURNOVERS', 'PT', 'Turnovers Per 32 Mins', 12, '	SELECT 	 PL.FNAME\r\n		,PL.NAME\r\n		,TM.CITYNAME\r\n		,TM.TEAMNAME\r\n		,TM.TEAMNUM\r\n		,PS.PLAYERID\r\n		,PS.STATS_TOTAL\r\n		,PS.STATS_TOTAL_1\r\n		,PS.STAT_PG\r\n		,PS.GAME_COUNT\r\n\r\n	FROM (\r\n	SELECT	 PS.PLAYERID AS PLAYERID\r\n		,SUM(PS.TURNOVERS) as STATS_TOTAL\r\n		,SUM(PS.MINS) as STATS_TOTAL_1\r\n		,(SUM(PS.TURNOVERS + 0.00) / SUM(PS.MINS + 0.00)) * 32 AS STAT_PG\r\n		,SUM(PS.PLAYED) as GAME_COUNT\r\n\r\n	FROM PLAYERSTATS PS\r\n\r\n	INNER JOIN SCHEDULE SH\r\n	ON PS.SCHEDULE_ID = SH.SCHEDULE_ID\r\n	AND PS.PLAYED = 1\r\n\r\n	INNER JOIN SEASON SE\r\n	ON SH.GAME_DATE BETWEEN SE.SEASON_SDTE AND SE.SEASON_EDTE\r\n	AND CURDATE() BETWEEN SE.SEASON_SDTE AND SE.SEASON_EDTE\r\n	GROUP BY 1\r\n\r\n	ORDER BY STAT_PG DESC, STATS_TOTAL\r\n	LIMIT 50) AS PS\r\n\r\n	INNER JOIN PLAYERS PL\r\n	ON PS.PLAYERID = PL.PLAYERID\r\n\r\n	INNER JOIN TEAMS TM\r\n	ON PL.TEAM = TM.TEAMNUM\r\n\r\n	ORDER BY PS.STAT_PG DESC, PS.STATS_TOTAL DESC, PS.GAME_COUNT, PL.NAME, PL.FNAME\r\n', 1);
INSERT INTO `stats_sql` VALUES ('OREBOUNDSDIFF', 'TDPG', 'Offensive Rebounds Diff Per Game', 8, 'SELECT	 TM.CITYNAME\r\n		,TM.TEAMNAME\r\n		,TM.TEAMNUM\r\n		,TM.IMAGENAME\r\n\r\n		,GST.TEAM_NUMBER as TEAMNUM\r\n		,(SUM(GST.OREBOUNDS) - SUM(GSO.OREBOUNDS)) / COUNT(*) as STAT_PG\r\n		,SUM(GST.OREBOUNDS) as STATS_TOTAL\r\n		,SUM(GST.OREBOUNDS + 0.00) / (COUNT(*) + 0.00) as STAT_PG_1\r\n		,SUM(GSO.OREBOUNDS) as STATS_TOTAL_2\r\n		,SUM(GSO.OREBOUNDS + 0.00) / (COUNT(*) + 0.00) as STAT_PG_2\r\n		,SUM(SH.PLAYED) as GAME_COUNT\r\n\r\nFROM GAMESTATS GST\r\nINNER JOIN GAMESTATS GSO\r\nON 	GST.SCHEDULE_ID = GSO.SCHEDULE_ID\r\nAND	GST.TEAM_NUMBER <> GSO.TEAM_NUMBER\r\n\r\nINNER JOIN SCHEDULE SH\r\nON SH.SCHEDULE_ID = GST.SCHEDULE_ID\r\nAND SH.PLAYED = 1\r\nAND SH.FORFEIT IS NULL\r\n\r\nINNER JOIN SEASON SE\r\nON SH.GAME_DATE BETWEEN SE.SEASON_SDTE AND SE.SEASON_EDTE\r\nAND CURDATE() BETWEEN SE.SEASON_SDTE AND SE.SEASON_EDTE\r\n\r\nINNER JOIN TEAMS TM\r\nON GST.TEAM_NUMBER = TM.TEAMNUM\r\n\r\ngroup by 1,2,3,4\r\n\r\nORDER BY STAT_PG DESC, STATS_TOTAL DESC, GAME_COUNT', 2);
