INSERT INTO `stats_sql` VALUES ('TURNOVERS', 'APG', 'Turnovers Per Game', 29, '	SELECT 	 PL.FNAME\r\n		,PL.NAME\r\n		,TM.CITYNAME\r\n		,TM.TEAMNAME\r\n		,TM.TEAMNUM\r\n		,PS.PLAYERID\r\n		,PS.STATS_TOTAL\r\n		,PS.STAT_PG\r\n		,PS.GAME_COUNT\r\n\r\n	FROM (\r\n	SELECT	 PS.PLAYERID AS PLAYERID\r\n		,SUM(PS.TURNOVERS) as STATS_TOTAL\r\n		,SUM(PS.TURNOVERS + 0.00) / (SUM(PS.PLAYED) + 0.00) as STAT_PG\r\n		,SUM(PS.PLAYED) as GAME_COUNT\r\n\r\n	FROM PLAYERSTATS PS\r\n\r\n	INNER JOIN SCHEDULE SH\r\n	ON PS.SCHEDULE_ID = SH.SCHEDULE_ID\r\n	AND PS.PLAYED = 1\r\n\r\n	INNER JOIN SEASON SE\r\n	ON SH.GAME_DATE BETWEEN SE.SEASON_SDTE AND SE.SEASON_EDTE\r\n\r\n	GROUP BY 1\r\n\r\n	ORDER BY STAT_PG DESC, STATS_TOTAL\r\n	LIMIT 50) AS PS\r\n\r\n	INNER JOIN PLAYERS PL\r\n	ON PS.PLAYERID = PL.PLAYERID\r\n\r\n	INNER JOIN TEAMS TM\r\n	ON PL.TEAM = TM.TEAMNUM\r\n\r\n	ORDER BY PS.STAT_PG DESC, PS.STATS_TOTAL DESC, PS.GAME_COUNT, PL.NAME, PL.FNAME\r\n', 3);
INSERT INTO `stats_sql` VALUES ('TURNOVERS', 'ATOT', 'Total Turnovers', 28, '	SELECT 	 PL.FNAME\r\n		,PL.NAME\r\n		,TM.CITYNAME\r\n		,TM.TEAMNAME\r\n		,TM.TEAMNUM\r\n		,PS.PLAYERID\r\n		,PS.STATS_TOTAL\r\n		,PS.STAT_PG\r\n		,PS.GAME_COUNT\r\n\r\n	FROM (\r\n	SELECT	 PS.PLAYERID AS PLAYERID\r\n		,SUM(PS.TURNOVERS) as STAT_PG\r\n		,SUM(PS.TURNOVERS + 0.00) / (COUNT(*) + 0.00) as STATS_TOTAL\r\n		,SUM(PS.PLAYED) as GAME_COUNT\r\n\r\n	FROM PLAYERSTATS PS\r\n\r\n	INNER JOIN SCHEDULE SH\r\n	ON PS.SCHEDULE_ID = SH.SCHEDULE_ID\r\n	AND PS.PLAYED = 1\r\n\r\n	INNER JOIN SEASON SE\r\n	ON SH.GAME_DATE BETWEEN SE.SEASON_SDTE AND SE.SEASON_EDTE\r\n\r\n	GROUP BY 1\r\n\r\n	ORDER BY STAT_PG DESC, STATS_TOTAL\r\n	LIMIT 50) AS PS\r\n\r\n	INNER JOIN PLAYERS PL\r\n	ON PS.PLAYERID = PL.PLAYERID\r\n\r\n	INNER JOIN TEAMS TM\r\n	ON PL.TEAM = TM.TEAMNUM\r\n\r\n	ORDER BY PS.STAT_PG DESC, PS.STATS_TOTAL DESC, PS.GAME_COUNT, PL.NAME, PL.FNAME\r\n', 3);
INSERT INTO `stats_sql` VALUES ('EFF', 'AEFF', 'Efficiency', 27, '		SELECT * FROM (\r\n		SELECT	 PLAYERID\r\n			,(((POINTS + REBOUNDS + ASSISTS + STEALS + BLOCKS) - ((FGA - FGM) + (FTA - FTM) + TURNOVERS)) + 0.00) / GAME_COUNT AS STAT_PG\r\n			,GAME_COUNT\r\n		FROM (\r\n		SELECT	 PS.PLAYERID\r\n			,SUM(PS.POINTS) AS POINTS\r\n			,SUM(PS.REBOUNDS) AS REBOUNDS\r\n			,SUM(PS.BLOCKS) AS BLOCKS\r\n			,SUM(PS.ASSISTS) AS ASSISTS\r\n			,SUM(PS.STEALS) AS STEALS\r\n			,SUM(PS.FGA) AS FGA\r\n			,SUM(PS.FGM) AS FGM\r\n			,SUM(PS.FTA) AS FTA\r\n			,SUM(PS.FTM) AS FTM\r\n			,SUM(PS.TURNOVERS) AS TURNOVERS\r\n			,SUM(PS.PLAYED) as GAME_COUNT\r\n		FROM PLAYERSTATS PS\r\n		GROUP BY 1\r\n		) AS B\r\n\r\n		ORDER BY STAT_PG DESC\r\n		LIMIT 50) AS C\r\n\r\n		INNER JOIN PLAYERS PL\r\n		ON C.PLAYERID = PL.PLAYERID\r\n\r\n		INNER JOIN TEAMS TM\r\n		ON PL.TEAM = TM.TEAMNUM\r\n		\r\n		order by STAT_PG DESC', 3);
INSERT INTO `stats_sql` VALUES ('TRIPLE', 'ATOT', 'Triple-doubles', 26, '		SELECT * FROM\r\n		(SELECT	 B.PLAYERID\r\n			,CASE WHEN TOT_POINTS + TOT_REBOUNDS + TOT_ASSISTS + TOT_STEALS + TOT_BLOCKS >= 3 THEN 3 ELSE 0 END AS STATS_TOTALS\r\n			,COUNT(*) as STATS_TOTAL\r\n			\r\n		FROM\r\n\r\n		(\r\n		SELECT 	 PS.PLAYERID\r\n				,PS.SCHEDULE_ID\r\n				,case when PS.POINTS >= 10 THEN 1 ELSE 0 end AS TOT_POINTS\r\n				,case when PS.REBOUNDS >= 10 THEN 1 ELSE 0 end AS TOT_REBOUNDS\r\n				,case when PS.ASSISTS >= 10 THEN 1 ELSE 0 end AS TOT_ASSISTS\r\n				,case when PS.STEALS >= 10 THEN 1 ELSE 0 end AS TOT_STEALS\r\n				,case when PS.BLOCKS >= 10 THEN 1 ELSE 0 end AS TOT_BLOCKS\r\n				,COUNT(*) AS ACHIEVED\r\n\r\n		FROM 	PLAYERSTATS PS\r\n\r\n		INNER JOIN SCHEDULE SH\r\n		ON PS.SCHEDULE_ID = SH.SCHEDULE_ID\r\n		AND PS.PLAYED = 1\r\n\r\n		INNER JOIN SEASON SE\r\n		ON SH.GAME_DATE BETWEEN SE.SEASON_SDTE AND SE.SEASON_EDTE\r\n\r\n		WHERE PS.PLAYED = 1\r\n		GROUP BY 1,2,3,4,5,6,7\r\n		) AS B\r\n\r\n		GROUP BY 1,2 LIMIT 50 ORDER BY STATS_TOTAL DESC) AS C\r\n\r\n		INNER JOIN PLAYERS PL\r\n		ON C.PLAYERID = PL.PLAYERID\r\n\r\n		INNER JOIN TEAMS TM\r\n		ON PL.TEAM = TM.TEAMNUM\r\n\r\n		WHERE STATS_TOTALS = 3', 3);
INSERT INTO `stats_sql` VALUES ('DOUBLE', 'ATOT', 'Double-doubles', 25, '		SELECT * FROM\r\n		(SELECT	 B.PLAYERID\r\n			,TOT_POINTS + TOT_REBOUNDS + TOT_ASSISTS + TOT_STEALS + TOT_BLOCKS AS STATS_TOTALS\r\n			,COUNT(*) as STATS_TOTAL\r\n			\r\n		FROM\r\n\r\n		(\r\n		SELECT 	 PS.PLAYERID\r\n				,PS.SCHEDULE_ID\r\n				,case when PS.POINTS >= 10 THEN 1 ELSE 0 end AS TOT_POINTS\r\n				,case when PS.REBOUNDS >= 10 THEN 1 ELSE 0 end AS TOT_REBOUNDS\r\n				,case when PS.ASSISTS >= 10 THEN 1 ELSE 0 end AS TOT_ASSISTS\r\n				,case when PS.STEALS >= 10 THEN 1 ELSE 0 end AS TOT_STEALS\r\n				,case when PS.BLOCKS >= 10 THEN 1 ELSE 0 end AS TOT_BLOCKS\r\n				,COUNT(*) AS ACHIEVED\r\n\r\n		FROM 	PLAYERSTATS PS\r\n\r\n		INNER JOIN SCHEDULE SH\r\n		ON PS.SCHEDULE_ID = SH.SCHEDULE_ID\r\n		AND PS.PLAYED = 1\r\n\r\n		INNER JOIN SEASON SE\r\n		ON SH.GAME_DATE BETWEEN SE.SEASON_SDTE AND SE.SEASON_EDTE\r\n\r\n		WHERE PS.PLAYED = 1\r\n		GROUP BY 1,2,3,4,5,6,7\r\n		) AS B\r\n\r\n		GROUP BY 1,2 LIMIT 50 ORDER BY STATS_TOTAL DESC) AS C\r\n\r\n		INNER JOIN PLAYERS PL\r\n		ON C.PLAYERID = PL.PLAYERID\r\n\r\n		INNER JOIN TEAMS TM\r\n		ON PL.TEAM = TM.TEAMNUM\r\n\r\n		WHERE STATS_TOTALS = 2', 3);
INSERT INTO `stats_sql` VALUES ('MINS', 'ATOT', 'Total Minutes', 24, '	SELECT 	 PL.FNAME\r\n		,PL.NAME\r\n		,TM.CITYNAME\r\n		,TM.TEAMNAME\r\n		,TM.TEAMNUM\r\n		,PS.PLAYERID\r\n		,PS.STATS_TOTAL\r\n		,PS.STAT_PG\r\n		,PS.GAME_COUNT\r\n\r\n	FROM (\r\n	SELECT	 PS.PLAYERID AS PLAYERID\r\n		,SUM(PS.MINS) as STAT_PG\r\n		,SUM(PS.MINS + 0.00) / (COUNT(*) + 0.00) as STATS_TOTAL\r\n		,SUM(PS.PLAYED) as GAME_COUNT\r\n\r\n	FROM PLAYERSTATS PS\r\n\r\n	INNER JOIN SCHEDULE SH\r\n	ON PS.SCHEDULE_ID = SH.SCHEDULE_ID\r\n	AND PS.PLAYED = 1\r\n\r\n	INNER JOIN SEASON SE\r\n	ON SH.GAME_DATE BETWEEN SE.SEASON_SDTE AND SE.SEASON_EDTE\r\n\r\n	GROUP BY 1\r\n\r\n	ORDER BY STAT_PG DESC, STATS_TOTAL\r\n	LIMIT 50) AS PS\r\n\r\n	INNER JOIN PLAYERS PL\r\n	ON PS.PLAYERID = PL.PLAYERID\r\n\r\n	INNER JOIN TEAMS TM\r\n	ON PL.TEAM = TM.TEAMNUM\r\n\r\n	ORDER BY PS.STAT_PG DESC, PS.STATS_TOTAL DESC, PS.GAME_COUNT, PL.NAME, PL.FNAME\r\n', 3);
